import React, { Component } from 'react';
import ImageSlider from '../../components/slider/ImageSlider';

class ProductDetails extends Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedSize: null,
            products: [], // Use an array to hold the products
            error: null,
            loading: true,// Track loading state
            filterId: null
        };
    }

    componentDidMount() {
        this.fetchData();
    }


    fetchData = async () => {
        try {
            const response = await fetch('https://file.notion.so/f/f/196736dd-250f-45b1-ac1f-c4302855a2f9/9ca54096-7702-4c4b-b352-792e83263668/data.json?table=block&id=6cac81a9-c4d1-40eb-b7f8-31319a4fcada&spaceId=196736dd-250f-45b1-ac1f-c4302855a2f9&expirationTimestamp=1727049600000&signature=RzBAJxL9HSchoHq6UH3KJqKT6D-S_p8MwxWA-DKtfdI&downloadName=data.json'); // Replace with your API URL

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const data = await response.json();



            this.setState({
                products: data.data.products,
                loading: false
            });
        } catch (error) {
            this.setState({ error: error.message, loading: false });
        }
    };
    // Method to filter products by ID
    filterProductsById = (id) => {
        const { products } = this.state;
        return products.find(product => product.id === id);
    };

    render() {
        const param = window.location.pathname.split("/")[2];

        const singleProduct = this.filterProductsById(param)

        const images = [
            'https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_2_720x.jpg?v=1612816087',
            'https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_1_720x.jpg?v=1612816087',
            'https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_3_720x.jpg?v=1612816087',
            'https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_5_720x.jpg?v=1612816087',
            'https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_4_720x.jpg?v=1612816087',
        ];



        return (
            <div>
                <div className='container'>
                    <div className='productDetails d-flex justify-content-around flex-wrap my-5'>
                        <div className='productDetailsGallery'>
                            <ImageSlider images={images} />
                        </div>
                        <div className='productDetailsContent'>
                            <h3>{singleProduct.name}</h3>
                            <div className='productSizes'>
                                <p>SIZE:</p>
                                <span className="d-flex align-items-center my-3">

                                    <div className="d-flex align-items-center  w-75 sizesContainer ">
                                        <label className="containerBlock">
                                            <input type="radio" name="size" value="XS" />
                                            <span className="checkmark">XS</span>
                                        </label>
                                        <label className="containerBlock">
                                            <input type="radio" name="size" value="S" />
                                            <span className="checkmark ">S</span>
                                        </label>
                                        <label className="containerBlock">
                                            <input type="radio" name="size" value="M" />
                                            <span className="checkmark">M</span>
                                        </label>
                                        <label className="containerBlock">
                                            <input type="radio" name="size" value="L" />
                                            <span className="checkmark">L</span>
                                        </label>

                                    </div>

                                </span>

                            </div>
                            <div className='productColors'>
                                <p>COLOR:</p>
                                <span className="d-flex align-items-center my-3">

                                    <div className="d-flex align-items-center  w-75 sizesContainer ">
                                        <label className="containerBlock" style={{ background: "#D3D2D5" }}>
                                            <input type="radio" name="color" value="#D3D2D5" />
                                            <span className="checkmark"></span>
                                        </label>
                                        <label className="containerBlock" style={{ background: "#2B2B2B" }}>
                                            <input type="radio" name="color" value="#2B2B2B" />
                                            <span className="checkmark"></span>

                                        </label>
                                        <label className="containerBlock" style={{ background: "#0F6450" }} >
                                            <input type="radio" name="color" value="#0F6450" />
                                            <span className="checkmark"></span>

                                        </label>

                                    </div>

                                </span>

                            </div>
                            <div className='productSizes'>
                                <p>PRICE:</p>

                                <p className="priceNumber"> $50.00</p>

                            </div>
                            <div className='my-4' >
                                <button className='cartBtn' type='submit'>ADD TO CART</button>

                            </div>
                            <div className='productDet'>
                                <p className=''>Find stunning women's cocktail dresses and party dresses. Stand out in lace and metallic cocktail dresses and party dresses from all your favorite brands.</p>


                            </div>


                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default ProductDetails;
