import React, { Component } from 'react';
import ProductBox from '../../components/product/ProductBox';
import { gql } from '@apollo/client';
import { Query } from '@apollo/client/react/components';

const GET_CATEGORY_PRODUCTS = gql`
    query GetCategoryProducts($categoryId: String!) {
        category(id: $categoryId) {
            id
            name
            products {
                id
                name
                price
                galleries {
                    image_url
                }
            }
        }
    }
`;

class CategoryPage extends Component {
    state = {
        categoryId: null,
    };

    componentDidMount() {
        // Extract the category ID from the URL
        const categoryId = parseInt(window.location.pathname.split("/")[2]); // Ensure the ID is an integer
        this.setState({ categoryId });
    }

    render() {
        const { categoryId } = this.state;
        console.log(categoryId)

        return (
            <div className='categoryPage'>
                <div className='container py-5'>
                    <h2>{categoryId ? `Category ID: ${categoryId}` : 'Loading...'}</h2>
                    {categoryId !== null && (
                        <Query query={GET_CATEGORY_PRODUCTS} variables={{ categoryId }}>
                            {({ loading, error, data }) => {
                                if (loading) return <p>Loading...</p>;
                                if (error) return <p>Error: {error.message}</p>;

                                const { category } = data;

                                if (category && category.products.length > 0) {
                                    return (
                                        <div className='row'>

                                        </div>
                                    );
                                } else {
                                    return <p>No products found in this category.</p>;
                                }
                            }}
                        </Query>
                    )}
                </div>
            </div>
        );
    }
}

export default CategoryPage;
