import React, { Component } from 'react';

export const GlobalStateContext = React.createContext();

class GlobalStateProvider extends Component {
    state = {
        cart: [], // initial cart state
    };

    addToCart = async (skuId) => {
        const ADD_TO_CART_MUTATION = `
            mutation add {
                addToCart(sku_id: ${skuId}) {
                    id
                    product {
                        name
                        price
                        image
                    }
                }
            }
        `;

        try {
            const response = await fetch('http://localhost/php_projects/scandiweb_store/backend/index.php', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json',
                },
                body: JSON.stringify({
                    query: ADD_TO_CART_MUTATION,
                    variables: { skuId },
                }),
            });

            const result = await response.json();

            if (result.errors) {
                console.error('Error adding item to cart:', result.errors);
            } else {
                const newCartItem = result.data.addToCart;

                this.setState((prevState) => ({
                    cart: [...prevState.cart, newCartItem] // update cart with new item
                }));
            }
        } catch (error) {
            console.error('Error adding item to cart:', error);
        }
    };

    render() {
        return (
            <GlobalStateContext.Provider
                value={{
                    cart: this.state.cart, // provide the cart state
                    addToCart: this.addToCart, // provide the addToCart function
                }}
            >
                {this.props.children}
            </GlobalStateContext.Provider>
        );
    }
}

export default GlobalStateProvider;
