import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import shoppingCart from "../../assets/images/cart.png";
import { gql } from '@apollo/client';
export default class ProductBox extends Component {

    componentDidMount() {




    }

    render() {







        const { image, name, price, id } = this.props;




        const addToCart = async (skuId) => {

            const ADD_TO_CART_MUTATION = gql`
                mutation  {
                    addToCart(sku_id: ${skuId}) {
                        id
                    }
                }
            `;

            const response = await fetch('http://localhost/php_projects/scandiweb_store/backend/index.php', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    // Include any authorization headers if required
                },
                body: JSON.stringify({
                    ADD_TO_CART_MUTATION,
                    variables: { skuId }
                }),
            });




            const result = await response.json();
            console.log(result)

            if (result.errors) {
                console.error('Error adding item to cart:', result.errors);
            } else {
                console.log('Item added to cart:');
            }
        };


        return (
            <div className='productBox'>
                <button className='addToCart bo' onClick={() => addToCart(id)}>
                    <img src={shoppingCart} alt='add to cart' />
                </button>
                <div className='productImageBox'>
                    <Link to={"/product/" + id}> <img src={image} className='productImage' alt='product' /></Link>
                </div>
                <p className='productName'>{name}</p>
                <p className='productPrice'>${price}</p>
            </div>
        )
    }
}
