import React, { Component } from 'react';
import { Query } from 'react-apollo';
import { GET_PRODUCT_DETAILS } from './queries';

class ProductDetails extends Component {
    render() {
        const { sku_id } = this.props.match.params;

        return (
            <Query query={GET_PRODUCT_DETAILS} variables={{ sku_id }}>
                {({ loading, error, data }) => {
                    if (loading) return <p>جاري تحميل البيانات...</p>;
                    if (error) return <p>حدث خطأ: {error.message}</p>;

                    const { product } = data;

                    return (
                        <div>
                            <h1>{product.name}</h1>
                            <p>{product.description}</p>
                            {/* عرض صور المنتج */}
                            <div>
                                {product.galleries.map(gallery => (
                                    <img key={gallery.image_url} src={gallery.image_url} alt={product.name} />
                                ))}
                            </div>
                            {/* عرض سعر المنتج */}
                            <p>السعر: {product.prices[0].currency_symbol} {product.prices[0].amount}</p>
                        </div>
                    );
                }}
            </Query>
        );
    }
}

export default ProductDetails;
