import React, { Component } from 'react';
import ProductBox from '../../components/product/ProductBox';
import { gql } from '@apollo/client';
import { Query } from '@apollo/client/react/components';

const GET_ALL_CATEGORIES = gql`
    query GetAllCategories {
        categories {
            id
            name
            products {
                id
                name
                price
                galleries {
                    image_url
                }
            }
        }
    }
`;

class CategoryPage extends Component {
    state = {
        categoryName: null,
    };

    componentDidMount() {
        // Extract the category name from the URL (assuming it's the third segment)
        const categoryName = window.location.pathname.split("/")[2];
        this.setState({ categoryName });
    }

    render() {
        const { categoryName } = this.state;

        return (
            <div className='categoryPage'>
                <div className='container py-5'>
                    <h2>{categoryName ? `${categoryName}` : 'Loading...'}</h2>
                    {categoryName && (
                        <Query query={GET_ALL_CATEGORIES}>
                            {({ loading, error, data }) => {
                                if (loading) return <p>Loading...</p>;
                                if (error) return <p>Error: {error.message}</p>;

                                // If categoryName is 'all', show products from all categories
                                if (categoryName.toLowerCase() === 'all') {
                                    // Collect all products from all categories
                                    const allProducts = data.categories.reduce((acc, cat) => {
                                        return acc.concat(cat.products);
                                    }, []);

                                    if (allProducts.length > 0) {
                                        return (
                                            <div className='row'>
                                                {allProducts.map((product) => (
                                                    <div className='col-12 col-lg-4' key={product.id}>
                                                        <ProductBox
                                                            name={product.name}
                                                            image={product.galleries[0]?.image_url || 'default-image-url'}
                                                            price={product.price}
                                                            id={product.id}
                                                        />
                                                    </div>
                                                ))}
                                            </div>
                                        );
                                    } else {
                                        return <p>No products found in all categories.</p>;
                                    }
                                }

                                // Find the specific category that matches the categoryName from the URL
                                const category = data.categories.find(cat => cat.name.toLowerCase() === categoryName.toLowerCase());

                                if (category && category.products.length > 0) {
                                    return (
                                        <div className='row'>
                                            {category.products.map((product) => (
                                                <div className='col-12 col-lg-4' key={product.id}>
                                                    <ProductBox
                                                        name={product.name}
                                                        image={product.galleries[0]?.image_url || 'default-image-url'}
                                                        price={product.price}
                                                        id={product.id}
                                                    />
                                                </div>
                                            ))}
                                        </div>
                                    );
                                } else {
                                    return <p>No products found in this category.</p>;
                                }
                            }}
                        </Query>
                    )}
                </div>
            </div>
        );
    }
}

export default CategoryPage;
