import React, { Component } from 'react'
import cart from "../../assets/images/shopping-cart.png";
import api from "../../locals/products.json";
export default class Cart extends Component {


    constructor(props) {
        super(props);
        this.state = {

            products: api.data.products, // Use an array to hold the products

            singleProduct: {}
        };
    }

    render() {



        return (
            <div>

                <button type="button" className="btn position-relative " data-bs-toggle="modal" data-bs-target="#exampleModal">
                    <span className='cartCount'>3</span>
                    <img src={cart} alt='cart icon' />
                </button>


                <div className="modal fade" id="exampleModal" >
                    <div className="modal-dialog">

                        <div className='cartHeading d-flex align-items-center'>
                            <h4>My Bag, </h4><p className='m-0'>3 items</p>

                        </div>
                        <div className='cartItemsContainer my-2'>
                            <div className='cartItem w-100'>

                                <div className='w-50 cartItemContent'>
                                    <h5 className='itemName'>Running Short </h5>
                                    <p className='m-0 itemPrice'>$50.00</p>
                                </div>
                                <div className='w-50'></div>

                            </div>

                        </div>
                    </div>
                </div>


            </div>
        )
    }
}
