import React, { Component } from 'react';
import ImageSlider from '../../components/slider/ImageSlider';
class ProductDetails extends Component {
    constructor(props) {
        super(props);
        this.state = {
            product: null,
            loading: true,
            error: null,
            sku_id: Number(window.location.pathname.split("/")[2]),  // إضافة sku_id إلى حالة المكون
        };
    }

    componentDidMount() {
        // const param = Number(window.location.pathname.split("/")[2] || null);

        this.fetchProductDetails();

    }

    fetchProductDetails = () => {
        const { sku_id } = this.state;

        console.log(sku_id);

        if (sku_id) {
            // كتابة استعلام GraphQL
            const query = `
                query GetProductDetails {
                    product(sku_id: ${sku_id}) {
                        id
                        name
                    
                        prices{
                        amount}
                       
                    }
                }
            `;

            // استخدام fetch لجلب البيانات من واجهة GraphQL API
            fetch('http://localhost/php_projects/scandiweb_store/backend/index.php', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    query: query,
                    variables: {
                        sku_id: 1
                    },
                }),
            })
                .then((response) => response.json())
                .then((data) => {
                    if (data.errors) {

                        this.setState({ error: data.errors[0].message, loading: false });
                    } else {
                        // تحديث الحالة بالبيانات المستلمة
                        this.setState({ product: data.data.product, loading: false });
                    }
                })
                .catch((error) => {
                    // التعامل مع أخطاء الشبكة أو التنفيذ
                    this.setState({ error: error.message, loading: false });
                });
        }
    };

    render() {
        const { product, loading, error } = this.state;

        if (loading) return <p>Loading...</p>;

        if (error) {
            return (
                <div>
                    <p>Error occurred:</p>
                    <p>{error}</p>
                </div>
            );
        }

        return (
            <div>
                <div className='container'>
                    <div className='productDetails d-flex justify-content-around flex-wrap my-5'>
                        <div className='productDetailsGallery'>
                            <ImageSlider images={single.gallery} />
                        </div>
                        <div className='productDetailsContent'>
                            <h3>{single.name}</h3>
                            {
                                single.attributes.map((attrItem) => {
                                    let content;
                                    switch (attrItem.id) {
                                        case "Color":
                                            content = (
                                                <div className='productColors'>
                                                    <p>{attrItem.name}:</p>
                                                    <div className="d-flex align-items-center w-75 sizesContainer my-2" key={attrItem.id}>
                                                        {attrItem.items.map((colorItem) => (
                                                            <label className="containerBlock colorItem" style={{ background: colorItem.value }}>
                                                                <input type="radio" name="color" value={colorItem.value} />
                                                                <span className="checkmark"></span>
                                                            </label>
                                                        ))}
                                                    </div>
                                                </div>
                                            );
                                            break;

                                        default:
                                            content = (
                                                <div className="productSizes my-2" key={attrItem.id}>
                                                    <p>{attrItem.name}:</p>
                                                    {attrItem.items.map((item) => (
                                                        <label className="containerBlock" key={item.id}>
                                                            <input type="radio" name={attrItem.name} value={item.value} />
                                                            <span className="checkmark">{item.value}</span>
                                                        </label>
                                                    ))}
                                                </div>
                                            );
                                            break;
                                    }

                                    return content;
                                })
                            }










                            {/*    <div className='productColors'>
                                <p>COLOR:</p>
                                <span className="d-flex align-items-center my-3">

                                    <div className="d-flex align-items-center  w-75 sizesContainer ">
                                        <label className="containerBlock" style={{ background: "#D3D2D5" }}>
                                            <input type="radio" name="color" value="#D3D2D5" />
                                            <span className="checkmark"></span>
                                        </label>
                                        <label className="containerBlock" style={{ background: "#2B2B2B" }}>
                                            <input type="radio" name="color" value="#2B2B2B" />
                                            <span className="checkmark"></span>

                                        </label>
                                        <label className="containerBlock" style={{ background: "#0F6450" }} >
                                            <input type="radio" name="color" value="#0F6450" />
                                            <span className="checkmark"></span>

                                        </label>

                                    </div>

                                </span>

                            </div>
*/}


                            {/*******************************************************************************************************/}
                            <div className='productSizes'>
                                <p className=''>PRICE:</p>

                                <p className="priceNumber"> {single.prices[0].currency.symbol}{single.prices[0].amount}</p>

                            </div>
                            <div className='my-4' >
                                <button className='cartBtn' type='submit'>ADD TO CART</button>
                            </div>
                            <div className='productDet'>
                                <p className=''>{single.description}</p>

                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default ProductDetails;
