import React, { Component } from 'react';
import { gql, useQuery } from '@apollo/client';
import ImageSlider from '../../components/slider/ImageSlider';

// GraphQL query to get product by sku_id
const GET_PRODUCT_BY_SKU_ID = gql`
  query getProductBySkuId($sku_id: Int!) {
    product(sku_id: $sku_id) {
      id
      name
      description
      galleries {
        image_url
      }
      prices {
       
          currency_symbol
       
        amount
      }
      attributes {
       
        name
        items {
          
          value
        }
      }
    }
  }
`;

const ProductDetails = (props) => {
    const param = parseInt(window.location.pathname.split("/")[2]); // Get sku_id from URL
    const { loading, error, data } = useQuery(GET_PRODUCT_BY_SKU_ID, {
        variables: { sku_id: param },
    });

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error: {error.message}</p>;

    const single = data.product;

    return (
        <div>
            <div className='container'>
                <div className='productDetails d-flex justify-content-around flex-wrap my-5'>
                    <div className='productDetailsGallery'>
                        <ImageSlider images={single.gallery.map(g => g.image_url)} />
                    </div>
                    <div className='productDetailsContent'>
                        <h3>{single.name}</h3>
                        {
                            single.attributes.map((attrItem) => {
                                let content;
                                switch (attrItem.name) {
                                    case "Color":
                                        content = (
                                            <div className='productColors'>
                                                <p>{attrItem.name}:</p>
                                                <div className="d-flex align-items-center w-75 sizesContainer my-2" key={attrItem.id}>
                                                    {attrItem.items.map((colorItem) => (
                                                        <label className="containerBlock colorItem" style={{ background: colorItem.value }} key={colorItem.id}>
                                                            <input type="radio" name="color" value={colorItem.value} />
                                                            <span className="checkmark"></span>
                                                        </label>
                                                    ))}
                                                </div>
                                            </div>
                                        );
                                        break;
                                    default:
                                        content = (
                                            <div className="productSizes my-2" key={attrItem.id}>
                                                <p>{attrItem.name}:</p>
                                                {attrItem.items.map((item) => (
                                                    <label className="containerBlock" key={item.id}>
                                                        <input type="radio" name={attrItem.name} value={item.value} />
                                                        <span className="checkmark">{item.value}</span>
                                                    </label>
                                                ))}
                                            </div>
                                        );
                                        break;
                                }

                                return content;
                            })
                        }

                        <div className='productSizes'>
                            <p className=''>PRICE:</p>
                            <p className="priceNumber">
                                {single.prices[0].currency.symbol}{single.prices[0].amount}
                            </p>
                        </div>

                        <div className='my-4'>
                            <button className='cartBtn' type='submit'>ADD TO CART</button>
                        </div>

                        <div className='productDet'>
                            <p className=''>{single.description}</p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProductDetails;
