import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import shoppingCart from "../../assets/images/cart.png";
import { gql } from '@apollo/client';
import { Mutation } from '@apollo/client/react/components';

export default class ProductBox extends Component {
    render() {
        const { image, name, price, id } = this.props; // Destructure props

        // Define the GraphQL mutation with a variable for sku_id
        const ADD_TO_CART_MUTATION = gql`
            mutation AddToCart {
                addToCart(sku_id: ${id}) {
                    id
                }
            }
        `;

        return (
            <Mutation mutation={ADD_TO_CART_MUTATION}>
                {(addToCart, { loading, error }) => (
                    <div className='productBox'>
                        <button
                            className='addToCart bo'
                            onClick={() => {
                                addToCart({ variables: { skuId: id } }) // Use skuId from props
                                    .then(response => {
                                        console.log('Item added to cart', response);
                                    })
                                    .catch(err => {
                                        console.error('Error adding item to cart', err);
                                    });
                            }}
                        >
                            <img src={shoppingCart} alt='add to cart' />
                        </button>
                        <div className='productImageBox'>
                            <Link to={`/product/${id}`}>
                                <img src={image} className='productImage' alt='product' />
                            </Link>
                        </div>
                        <p className='productName'>{name}</p>
                        <p className='productPrice'>${price}</p>
                        {loading && <p>Loading...</p>}
                        {error && <p>Error: {error.message}</p>}
                    </div>
                )}
            </Mutation>
        );
    }
}
