import React, { Component } from 'react';
import { GlobalStateContext } from '../../GlobalStateContext'; // استيراد الـ context
import { Link } from 'react-router-dom';
import shoppingCart from "../../assets/images/cart.png";

export default class ProductBox extends Component {
    static contextType = GlobalStateContext; // Access context in a class component

    addToCartHandler = () => {
        const { addToCart } = this.context; // Access addToCart function from context
        const { id } = this.props; // Get product ID from props

        addToCart(id); // Add product to cart
    }
