import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import shoppingCart from "../../assets/images/cart.png";
export default class ProductBox extends Component {
    addToCart = async (skuId) => {
        const ADD_TO_CART_MUTATION = `
            mutation add {
                addToCart(sku_id: ${skuId}) {
                    id
                    sku_id
                    product {
                        name
                    }
                    price
                    quantity
                }
            }
        `;

        const response = await fetch('http://localhost/php_projects/scandiweb_store/backend/index.php', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json',
            },
            body: JSON.stringify({
                query: ADD_TO_CART_MUTATION,
                variables: { skuId },
            }),
        });

        const result = await response.json();
        if (result.errors) {
            console.error('Error adding item to cart:', result.errors);
        } else {
            // تحديث السلة في context بعد إضافة المنتج
            const newItem = result.data.addToCart;
            this.context.setCart([...this.context.cart, newItem]); // إضافة المنتج الجديد للسلة
            this.props.onCartUpdate();
        }
    };
    render() {
        const { image, name, price, id, stock } = this.props;
        return (
            <div className='productBox'>
                <button className='addToCart bo' onClick={this.addToCart}>
                    <img src={shoppingCart} alt='add to cart' />
                </button>
                {stock ? <span className='stockStatus'>
                    <p>OUT OF STOCK</p>
                </span> : ""}
                <div className='productImageBox'>
                    <Link to={"/product/" + id}> <img src={image} className='productImage' alt='product' /></Link>
                </div>
                <p className='productName'>{name}</p>
                <p className='productPrice'>${price}</p>
            </div>
        )
    }
}
