import React, { Component } from 'react';
import { useQuery } from '@apollo/client';
import logo from "../../assets/images/logo.png";
import Cart from '../cart/Cart';
import { gql } from '@apollo/client';

// استعلام لجلب أسماء الفئات
const GET_CATEGORIES = gql`
    query GetCategories {
        categories {
            name
        }
    }
`;

// مكون Header
const Header = (props) => {
    const { items, params } = props;

    // تنفيذ الاستعلام
    const { loading, error, data } = useQuery(GET_CATEGORIES);

    if (loading) return <p>Loading categories...</p>;
    if (error) return <p>Error: {error.message}</p>;

    // استخراج الفئات من البيانات
    const categories = data.categories;

    return (
        <header>
            <nav className="navbar navbar-expand-lg ">
                <div className="container">
                    <div className="collapse navbar-collapse d-flex justify-content-between" id="navbarNav">
                        <ul className="m-0 d-flex align-items-center pt-3">
                            {categories.map((category) => (
                                <li
                                    data-testid='category-link'
                                    className={params === category.name ? "nav-item active" : "nav-item"}
                                    key={category.name}
                                >
                                    <a
                                        className="nav-link"
                                        aria-current="page"
                                        href={"/category/" + category.name}
                                        data-testid={params === category.name ? 'active-category-link' : 'category-link'}
                                    >
                                        {category.name}
                                    </a>
                                </li>
                            ))}
                        </ul>
                        <div>
                            <a className="navbar-brand" href={items[0].to}>
                                <img src={logo} alt='logo' />
                            </a>
                        </div>
                        <div className='shoppingCart'>
                            <Cart />
                        </div>
                    </div>
                </div>
            </nav>
        </header>
    );
};

export default Header;
