import { gql } from '@apollo/client';

export const GET_PRODUCT_DETAILS = gql`{
 
 query getProductWithReviews($sku_id) {
    product(sku_id: $sku_id) {   
      name
      description
     
}
      }
}`;

export const GET_ALL_PRODUCTS = gql`
  query getAllProducts {
    products {
      id
      name
      price
      category
    }
  }
`;
