import React, { Component } from 'react';
import ProductBox from '../../components/product/ProductBox';
import response from "../../locals/products.json";
class CategoryPage extends Component {
    state = {
        param: window.location.pathname.split("/")[2],


    }

    filterCategoryByName = (categoryName) => {
        const { products } = this.state;
        // return products.find(product => product.id === id);
    };

    render() {
        const param = window.location.pathname.split("/")[2];
        this.state = {
            products: response.data.products
        }




        return (
            <div className='categoryPage'>
                <div className='container py-5'>
                    <h2>{param}</h2>
                    <div className='row'>
                        {this.state.products.map((product) => (<div className='col-12 col-lg-4' key={product.id}>
                            <ProductBox name={product.name} image={product.gallery[0]} price={product.prices[0].amount} id={product.id} />
                        </div>))}
                    </div>
                </div>
            </div>
        );
    }
}

export default CategoryPage;
