import React, { Component } from 'react';
import { gql } from '@apollo/client';
import { Query } from '@apollo/client/react/components';
import ImageSlider from '../../components/slider/ImageSlider';

// GraphQL query to get product by sku_id
const GET_PRODUCT_BY_SKU_ID = gql`
  query getProductBySkuId($sku_id: Int!) {
    product(sku_id: $sku_id) {
      id
      name
      description
      galleries {
        image_url
      }
      prices {
        currency_symbol
        amount
      }
      attributes {
        name
        items {
          value
        }
      }
    }
  }
`;

class ProductDetails extends Component {
    state = {
        skuId: null,

    };

    componentDidMount() {
        const param = parseInt(window.location.pathname.split('/')[2]);
        this.setState({ skuId: param });
        console.log(param)
    }

    // fetchSkuId() {



    //     if (!isNaN(param)) {
    //         this.setState({ skuId: param }, () => {
    //             this.fetchProduct(); // Fetch product after setting skuId
    //         });
    //     } else {
    //         this.setState({ error: "SKU ID is not valid.", loading: false });
    //     }
    // }

    // async fetchProduct() {
    //     const { skuId } = this.state;
    //     console.log("SKU ID before query:", skuId); // تسجيل قبل الاستعلام

    //     if (skuId) {
    //         try {


    //             this.setState({

    //                 loading: false,
    //             });
    //         } catch (error) {
    //             this.setState({
    //                 error: error.message,
    //                 loading: false,
    //             });
    //         }
    //     } else {
    //         this.setState({ loading: false, error: "SKU ID is not valid." });
    //     }
    // }

    render() {
        const { loading, error, product } = this.state;

        if (loading) return <p>Loading...</p>;
        if (error) return <p>Error: {error}</p>;



        return (
            <div>
                <div className='container'>


                    <div className='productDetails d-flex justify-content-around flex-wrap my-5'>
                        <div className='productDetailsGallery'>
                            <ImageSlider images={product.galleries.map(g => g.image_url)} />
                        </div>
                        <div className='productDetailsContent'>
                            <h3>{product.name}</h3>
                            {
                                product.attributes.map((attrItem) => {
                                    let content;
                                    switch (attrItem.name) {
                                        case "Color":
                                            content = (
                                                <div className='productColors' key={attrItem.name}>
                                                    <p>{attrItem.name}:</p>
                                                    <div className="d-flex align-items-center w-75 sizesContainer my-2">
                                                        {attrItem.items.map((colorItem) => (
                                                            <label className="containerBlock colorItem" style={{ background: colorItem.value }} key={colorItem.value}>
                                                                <input type="radio" name="color" value={colorItem.value} />
                                                                <span className="checkmark"></span>
                                                            </label>
                                                        ))}
                                                    </div>
                                                </div>
                                            );
                                            break;
                                        default:
                                            content = (
                                                <div className="productSizes my-2" key={attrItem.name}>
                                                    <p>{attrItem.name}:</p>
                                                    {attrItem.items.map((item) => (
                                                        <label className="containerBlock" key={item.value}>
                                                            <input type="radio" name={attrItem.name} value={item.value} />
                                                            <span className="checkmark">{item.value}</span>
                                                        </label>
                                                    ))}
                                                </div>
                                            );
                                            break;
                                    }

                                    return content;
                                })
                            }

                            <div className='productSizes'>
                                <p className=''>PRICE:</p>
                                <p className="priceNumber">
                                    {product.prices[0].currency_symbol}{product.prices[0].amount}
                                </p>
                            </div>

                            <div className='my-4'>
                                <button className='cartBtn' type='submit'>ADD TO CART</button>
                            </div>

                            <div className='productDet'>
                                <p className=''>{product.description}</p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default ProductDetails;
