import React, { Component } from 'react';
import { gql, useQuery } from '@apollo/client';
import { Query } from '@apollo/client/react/components';
import ImageSlider from '../../components/slider/ImageSlider';

// GraphQL query to get product by sku_id
const GET_PRODUCT_BY_SKU_ID = gql`
    query GetProductBySkuId ($sku_id: Int!) {
        product(sku_id: $sku_id) {
        id
        name
        description
        galleries {
            image_url
        }
        prices {
            currency_symbol
            amount
        }
    }
  }
`;

class ProductDetails extends Component {


    constructor(props) {
        super(props);
        this.state = { skuId: parseInt(window.location.pathname.split('/')[2], 10) || null };
    }

    componentDidUpdate(prevProps, prevState) {
        if (prevState.skuId !== this.state.skuId) {
            const param = parseInt(window.location.pathname.split('/')[2], 10);
            this.setState({ skuId: Number(param) });
        }
    }

    // fetchSkuId() {



    //     if (!isNaN(param)) {
    //         this.setState({ skuId: param }, () => {
    //             this.fetchProduct(); // Fetch product after setting skuId
    //         });
    //     } else {
    //         this.setState({ error: "SKU ID is not valid.", loading: false });
    //     }
    // }

    // async fetchProduct() {
    //     const { skuId } = this.state;
    //     console.log("SKU ID before query:", skuId); // تسجيل قبل الاستعلام

    //     if (skuId) {
    //         try {


    //             this.setState({

    //                 loading: false,
    //             });
    //         } catch (error) {
    //             this.setState({
    //                 error: error.message,
    //                 loading: false,
    //             });
    //         }
    //     } else {
    //         this.setState({ loading: false, error: "SKU ID is not valid." });
    //     }
    // }

    render() {
        const { skuId } = this.state;
        if (!skuId) return <p>Loading SKU ID...</p>;
        console.log("state", this.state);

        return (
            <div>
                <div className='container'>
                    <div className='productDetails d-flex justify-content-around flex-wrap my-5'>
                        <Query query={GET_PRODUCT_BY_SKU_ID} variables={{ sku_id: Number(skuId) }}>
                            {({ loading, error, data }) => {
                                console.log("error", error);
                                if (loading) return <p>Loading...</p>;
                                if (error) return <p>Error: {error.message}</p>;
                            }}
                        </Query>
                    </div>
                </div>
            </div>
        );
    }
}

export default ProductDetails;


