import React, { Component } from 'react';
import ProductBox from '../../components/product/ProductBox';
import { gql } from '@apollo/client';
import { Query } from '@apollo/client/react/components';

const GET_ALL_PRODUCTS_WITH_CATEGORIES = gql`
    query GetAllProductsWithCategories {
         products {
            category{
            id
                name
            }
    id
    name
    price
  
    galleries {
      product_id
      image_url
    }
  }
    }
`;

class CategoryPage extends Component {
    state = {
        categoryName: null,

    };

    componentDidMount() {
        // Extract the category name from the URL (assuming it's the third segment)
        const categoryName = window.location.pathname.split("/")[2];
        this.setState({ categoryName });

    }

    render() {
        const { categoryName } = this.state;

        return (
            <div className='categoryPage'>
                <div className='container py-5'>
                    <h2>{categoryName ? `${categoryName}` : 'Loading...'}</h2>

                    <Query query={GET_ALL_PRODUCTS_WITH_CATEGORIES}>
                        {({ loading, error, data }) => {
                            if (loading) return <p>Loading...</p>;
                            if (error) return <p>Error: {error.message}</p>;


                            this.setState(data.products.category)
                            if (categoryName === 'all') {
                                const allProducts = data.products;
                                console.log(allProducts[0].galleries[0])
                                if (allProducts.length > 0) {
                                    return (
                                        <div className='row'>
                                            {allProducts.map((product) => (
                                                <div className='col-12 col-lg-4' key={product.id}>
                                                    <ProductBox
                                                        name={product.name}
                                                        image={product.galleries[0].image_url}
                                                        price={product.price}
                                                        id={product.id}
                                                    />
                                                </div>
                                            ))}
                                        </div>
                                    );
                                } else {
                                    return <p>No products found in all categories.</p>;
                                }


                            }

                        }}
                    </Query>
                </div>
            </div>
        );
    }
}

export default CategoryPage;

