import React, { Component } from 'react';
import { Query } from '@apollo/client/react/components';
import { GET_PRODUCT_DETAILS } from '../../graphql/queries.js';

class ProductDetails extends Component {
    render() {
        const param = Number(window.location.pathname.split("/")[2] || null);
        const sku_id = parseInt(param, 10)
        if (typeof (sku_id) == NaN) {

            console.log("nan")

        }

        return (
            <Query query={GET_PRODUCT_DETAILS} variables={{ sku_id }}>{

            }
                {({ loading, error, data }) => {

                    if (loading) return <p>loading...</p>;

                    if (error) {
                        console.error("Error details:", error);  // Print error details to console for debugging
                        if (error.networkError) {
                            return <p>Network error: {error.networkError.message}</p>;
                        }

                        if (error.graphQLErrors) {
                            return (
                                <div>
                                    <p>GraphQL query error:</p>
                                    {error.graphQLErrors.map(({ message, locations, path }, index) => (
                                        <div key={index}>
                                            <p>Error message: {message}</p>
                                            <p>Location: {locations ? locations.map(loc => `Line (${loc.line}, Column ${loc.column})`).join(', ') : 'Not available'}</p>
                                            <p>Path: {path ? path.join(' > ') : 'Not available'}</p>
                                        </div>
                                    ))}
                                </div>
                            );
                        }

                        return <p>An unknown error occurred.</p>;
                    }




                    const { product } = data;

                    return (
                        <div>
                            {product ? (
                                <h1></h1>
                            ) : (
                                <p>لم يتم العثور على المنتج</p>
                            )}
                        </div>
                    );
                }}
            </Query>
        );
    }
}

export default ProductDetails;
