import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import shoppingCart from "../../assets/images/cart.png";

export default class ProductBox extends Component {

    addToCart = async (skuId) => {
        // Write the mutation as a plain string
        const ADD_TO_CART_MUTATION = `gql
            mutation {
                addToCart(sku_id: ${skuId}) {
                    id
                }
            }
        `;

        // Send the mutation through fetch
        const response = await fetch('http://localhost/php_projects/scandiweb_store/backend/index.php', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json',
            },
            body: JSON.stringify({
                query: ADD_TO_CART_MUTATION, // Provide the query as a plain string
                variables: { skuId }, // Pass the variables object
            }),
        });

        const result = await response.json();
        console.log(result);

        if (result.errors) {
            console.error('Error adding item to cart:', result.errors);
        } else {
            console.log('Item added to cart:', result.data.addToCart);
        }
    };

    render() {
        const { image, name, price, id } = this.props;

        return (
            <div className='productBox'>
                <button className='addToCart bo' onClick={() => this.addToCart(id)}>
                    <img src={shoppingCart} alt='add to cart' />
                </button>
                <div className='productImageBox'>
                    <Link to={"/product/" + id}>
                        <img src={image} className='productImage' alt='product' />
                    </Link>
                </div>
                <p className='productName'>{name}</p>
                <p className='productPrice'>${price}</p>
            </div>
        );
    }
}
