import React, { Component } from 'react';
import ProductBox from '../../components/product/ProductBox';
import { gql } from '@apollo/client';
import { Query } from '@apollo/client/react/components';

const GET_CATEGORY_PRODUCTS = gql`
    query GetCategoryProducts($categoryName: String!) {
        category(name: $categoryName) { // Assuming your backend has a category query that fetches by name
            id
            name
            products {
                id
                name
                price
                galleries {
                    image_url
                }
            }
        }
    }
`;

class CategoryPage extends Component {
    state = {
        param: window.location.pathname.split("/")[2], // Extract the category from the URL
    }

    render() {
        const { param } = this.state;

        return (
            <div className='categoryPage'>
                <div className='container py-5'>
                    <h2>{param}</h2>
                    <Query query={GET_CATEGORY_PRODUCTS} variables={{ categoryName: param }}>
                        {({ loading, error, data }) => {
                            if (loading) return <p>Loading...</p>;
                            if (error) return <p>Error: {error.message}</p>;

                            const products = data.category.products;
                            console.log(products);
                            return (
                                <div className='row'>
                                    {products.map((product) => (
                                        <div className='col-12 col-lg-4' key={product.id}>
                                            <ProductBox
                                                name={product.name}
                                                image={product.galleries[0].image_url}
                                                price={product.price}
                                                id={product.id}
                                            />
                                        </div>
                                    ))}
                                </div>
                            );
                        }}
                    </Query>
                </div>
            </div>
        );
    }
}

export default CategoryPage;
