import React, { Component } from 'react';
import { Query } from '@apollo/client/react/components';
import { GET_PRODUCT_DETAILS } from '../../graphql/queries.js';

class ProductDetails extends Component {
    render() {
        const { sku_id } = window.location.pathname.split("/")[2];

        return (
            <Query query={GET_PRODUCT_DETAILS} variables={1}>
                {({ loading, error, data }) => {
                    if (loading) return <p>جاري تحميل البيانات...</p>;
                    if (error) return <p>حدث خطأ: {error.message}</p>;

                    const { product } = data;

                    return (
                        <div>
                            {product ? (
                                <h1>{product.name}</h1>
                            ) : (
                                <p>لم يتم العثور على المنتج</p>
                            )}
                        </div>
                    );
                }}
            </Query>
        );
    }
}

export default ProductDetails;
