import React, { Component } from 'react';
import ProductBox from '../../components/product/ProductBox';
import { gql } from '@apollo/client';
import { Query } from '@apollo/client/react/components';

const GET_ALL_PRODUCTS_WITH_CATEGORIES = gql`
    query GetAllProductsWithCategories {
         products {
            category{
            id
                name
            }
    id
    name
    price
  
    galleries {
      product_id
      image_url
    }
  }
    }
`;

class CategoryPage extends Component {
    state = {
        categoryName: null,
        products: []
    };

    componentDidMount() {
        // Extract the category name from the URL (assuming it's the third segment)
        const categoryName = window.location.pathname.split("/")[2];
        this.setState({ categoryName });

    }

    render() {
        const { categoryName } = this.state;

        return (
            <div className='categoryPage'>
                <div className='container py-5'>
                    <h2>{categoryName ? `${categoryName}` : 'Loading...'}</h2>

                    <Query query={GET_ALL_PRODUCTS_WITH_CATEGORIES}></Query>
                </div>
            </div>
        );
    }
}

export default CategoryPage;
