import React, { useEffect, useState } from 'react';
import ProductBox from '../../components/product/ProductBox';
import { gql, useQuery } from '@apollo/client';

const GET_ALL_CATEGORIES = gql`
    query GetAllCategories {
        categories {
            id
            name
            products {
                id
                name
                price
                galleries {
                    image_url
                }
            }
        }
    }
`;

const CategoryPage = () => {
    const [categoryName, setCategoryName] = useState(null);

    // استخدام useEffect لاستخراج اسم الفئة من URL عند تحميل المكون
    useEffect(() => {
        const name = window.location.pathname.split("/")[2];
        setCategoryName(name);
    }, []);

    // استخدام useQuery لجلب البيانات
    const { loading, error, data } = useQuery(GET_ALL_CATEGORIES);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error: {error.message}</p>;

    // إيجاد الفئة التي تطابق اسم الفئة من URL
    const category = data.categories.find(cat => cat.name.toLowerCase() === categoryName?.toLowerCase());

    return (
        <div className='categoryPage'>
            <div className='container py-5'>
                <h2>{categoryName ? `Category: ${categoryName}` : 'Loading...'}</h2>
                {category && category.products.length > 0 ? (
                    <div className='row'>
                        {category.products.map((product) => (
                            <div className='col-12 col-lg-4' key={product.id}>
                                <ProductBox
                                    name={product.name}
                                    image={product.galleries[0]?.image_url} // استخدام الصورة الأولى من المعرض
                                    price={product.price}
                                    id={product.id}
                                />
                            </div>
                        ))}
                    </div>
                ) : (
                    <p>No products found in this category.</p>
                )}
            </div>
        </div>
    );
};

export default CategoryPage;
