import React, { Component } from 'react';
import { ApolloClient, InMemoryCache, gql } from '@apollo/client';
import { GET_PRODUCT_DETAILS } from '../../graphql/queries.js'; // استعلام GraphQL الخاص بك

class ProductDetails extends Component {
    constructor(props) {
        super(props);
        this.state = {
            product: null,
            loading: true,
            error: null,
        };

        // إعداد Apollo Client
        this.client = new ApolloClient({
            uri: 'http://localhost/php_projects/scandiweb_store/backend/index.php', // ضع رابط GraphQL API الخاص بك هنا
            cache: new InMemoryCache(),
        });
    }

    componentDidMount() {
        const param = Number(window.location.pathname.split("/")[2] || null);
        const sku_id = parseInt(param, 10);

        this.fetchProductDetails(sku_id);
    }

    fetchProductDetails = (sku_id) => {
        this.client
            .query({
                query: GET_PRODUCT_DETAILS,
                variables: { sku_id },
            })
            .then((response) => {
                this.setState({
                    product: response.data.product,
                    loading: false,
                    error: null,
                });
            })
            .catch((error) => {
                this.setState({
                    loading: false,
                    error: error,
                });
            });
    };

    render() {
        const { product, loading, error } = this.state;

        if (loading) return <p>Loading...</p>;

        if (error) {
            return (
                <div>
                    <p>Error occurred:</p>
                    <p>{error.message}</p>
                </div>
            );
        }

        return (
            <div>
                {product ? (
                    <div>
                        <h1>{product.name}</h1>
                        <p>{product.description}</p>
                        {/* باقي تفاصيل المنتج */}
                    </div>
                ) : (
                    <p>لم يتم العثور على المنتج</p>
                )}
            </div>
        );
    }
}

export default ProductDetails;
