import React, { Component } from 'react';

class ProductDetails extends Component {
    constructor(props) {
        super(props);
        this.state = {
            product: null,
            loading: true,
            error: null,
            sku_id: Number(window.location.pathname.split("/")[2]),  // إضافة sku_id إلى حالة المكون
        };
    }

    componentDidMount() {
        // const param = Number(window.location.pathname.split("/")[2] || null);

        this.fetchProductDetails();

    }

    fetchProductDetails = () => {
        const { sku_id } = this.state;

        console.log(sku_id);

        if (sku_id) {
            // كتابة استعلام GraphQL
            const query = `
                query GetProductDetails($sku_id: Int!) {
                    product(sku_id: $sku_id) {
                        id
                        name
                    
                        prices{
                        amount}
                       
                    }
                }
            `;

            // استخدام fetch لجلب البيانات من واجهة GraphQL API
            fetch('http://localhost/php_projects/scandiweb_store/backend/index.php', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    query: query,
                    variables: {
                        sku_id: sku_id
                    },
                }),
            })
                .then((response) => response.json())
                .then((data) => {
                    if (data.errors) {

                        this.setState({ error: data.errors[0].message, loading: false });
                    } else {
                        // تحديث الحالة بالبيانات المستلمة
                        this.setState({ product: data.data.product, loading: false });
                    }
                })
                .catch((error) => {
                    // التعامل مع أخطاء الشبكة أو التنفيذ
                    this.setState({ error: error.message, loading: false });
                });
        }
    };

    render() {
        const { product, loading, error } = this.state;

        if (loading) return <p>Loading...</p>;

        if (error) {
            return (
                <div>
                    <p>Error occurred:</p>
                    <p>{error}</p>
                </div>
            );
        }

        return (
            <div>
                <h2>Product Details</h2>
                {product ? (
                    <div>
                        <p>Name: {product.name}</p>
                        <p>SKU: {product.sku}</p>
                        <p>Price: {product.price}</p>
                        <p>Type: {product.type}</p>
                    </div>
                ) : (
                    <p>Product not found</p>
                )}
            </div>
        );
    }
}

export default ProductDetails;
