import React, { Component } from 'react';
import ProductBox from '../../components/product/ProductBox';
import response from "../../locals/products.json";

class CategoryPage extends Component {
    state = {
        param: window.location.pathname.split("/")[2], // Extract the category from the URL
        products: response.data.products, // Store all products in the state
        filteredProducts: [] // To store the filtered products
    }

    // Function to filter products based on the category name
    filterCategoryByName = (categoryName) => {
        const filteredProducts = this.state.products.filter(product =>
            product.category === categoryName
        );
        this.setState({ filteredProducts }); // Update the filtered products in the state
    };

    componentDidMount() {
        // Filter products when the component is mounted, based on the URL parameter
        this.filterCategoryByName(this.state.param);
    }

    componentDidUpdate(prevProps, prevState) {
        // Check if the param has changed (in case of route change) and re-filter
        if (prevState.param !== this.state.param) {
            this.filterCategoryByName(this.state.param);
        }
    }

    render() {
        const { param, filteredProducts } = this.state;

        return (
            <div className='categoryPage'>
                <div className='container py-5'>
                    <h2>{param}</h2> {/* Display the current category */}
                    <div className='row'>
                        {/* Map over filtered products instead of all products */}
                        {filteredProducts.map((product) => (
                            <div className='col-12 col-lg-4' key={product.id}>
                                <ProductBox
                                    name={product.name}
                                    image={product.gallery[0]}
                                    price={product.prices[0].amount}
                                    id={product.id}
                                />
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        );
    }
}

export default CategoryPage;
