import React, { Component } from 'react';
import { Query } from '@apollo/client/react/components';
import { GET_PRODUCT_DETAILS } from '../../graphql/queries.js';

class ProductDetails extends Component {
    render() {
        const param = Number(window.location.pathname.split("/")[2] || null);
        const sku_id = parseInt(param, 10)
        console.log(sku_id)

        return (
            <Query query={GET_PRODUCT_DETAILS} variables={{ sku_id }}>{

            }
                {({ loading, error, data }) => {

                    if (loading) return <p>جاري تحميل البيانات...</p>;

                    if (error) {
                        console.error("Error details:", error);  // طباعة التفاصيل في console للتصحيح
                        if (error.networkError) {
                            return <p>حدث خطأ في الشبكة: {error.networkError.message}</p>;
                        }

                        if (error.graphQLErrors) {
                            return (
                                <div>
                                    <p>حدث خطأ في استعلام GraphQL:</p>
                                    {error.graphQLErrors.map(({ message, locations, path }, index) => (
                                        <div key={index}>
                                            <p>رسالة الخطأ: {message}</p>
                                            <p>الموقع: {locations ? locations.map(loc => `خط (${loc.line}, عمود ${loc.column})`).join(', ') : 'غير متوفر'}</p>
                                            <p>المسار: {path ? path.join(' > ') : 'غير متوفر'}</p>
                                        </div>
                                    ))}
                                </div>
                            );
                        }

                        return <p>حدث خطأ غير معروف.</p>;
                    }

                    const { product } = data;

                    return (
                        <div>
                            {product ? (
                                <h1></h1>
                            ) : (
                                <p>لم يتم العثور على المنتج</p>
                            )}
                        </div>
                    );
                }}
            </Query>
        );
    }
}

export default ProductDetails;
